mapper.select:
select collectionCatId, collectionCatName, collectionCatNum, parentCollectionCatId, parentCollectionCatNum, orderNum
from collection_cat
where collectionCatId = #{collectionCatId}

select count(1)
from collection_cat
where collectionCatName = #{collectionCatName}

select ifNull(parentCollectionCatNum, "")
from collection_cat
where collectionCatId = #{collectionCatId}

select collectionCatNum
from collection_cat
where collectionCatId = #{collectionCatId}

select collectionId, collectionName, collectionUrl, collectionCatId, remark
from collection
where collectionId = #{collectionId}
